INSERT INTO public.raw_items VALUES (527, 7, 'https://www.fearthefin.com/?p=22136', 'scheduled', 'https://www.fearthefin.com/sharks-foundation-and-norcal-special-olympics-host-unforgetabble-field-hockey-game/', 'https://www.fearthefin.com/sharks-foundation-and-norcal-special-olympics-host-unforgetabble-field-hockey-game/', 'Sharks Foundation and Norcal Special Olympics host unforgetabble field hockey game', '<p>The San Jose Sharks joined the <a href="https://sonc.org/">Special Olympics Northern California</a> field hockey athletes at the Santa Clara High School gymnasium Saturday afternoon for a riveting game. Organized by the Sharks Foundation, the game featured energy-filled highlights with Sharks Players (Mario Ferraro, Barclay Goodrow, Alexander Wennberg, William Eklund, Adam Gaudette, and Vincent Iorio) joining the field hockey athletes for an unforgettable afternoon.</p>
